pub fn part1(input: []const u8) isize {
    var floor: isize = 0;

    for (input) |char| {
        if (char == '(') {
            floor += 1;
        } else {
            floor -= 1;
        }
    }
    return floor;
}
pub fn part2(input: []const u8) isize {
    var position: isize = 1;
    var floor: isize = 0;

    for (input) |char| {
        if (char == '(') {
            floor += 1;
        } else {
            floor -= 1;
        }

        if (floor == -1) {
            break;
        }
        position += 1;
    }

    return position;
}

pub fn main() void {
    std.debug.print("Part 1: {d}\n", .{part1(INPUT)});
    std.debug.print("Part 2: {d}\n", .{part2(INPUT)});
}

test "part1" {
    const Case = struct {
        input: []const u8,
        expected: isize,
    };
    var cases = std.ArrayList(Case).init(std.testing.allocator);
    defer cases.deinit();

    try cases.append(.{
        .input = "(())",
        .expected = 0,
    });
    try cases.append(.{
        .input = "()()",
        .expected = 0,
    });
    try cases.append(.{
        .input = "(((",
        .expected = 3,
    });
    try cases.append(.{
        .input = "(()(()(",
        .expected = 3,
    });
    try cases.append(.{
        .input = "))(((((",
        .expected = 3,
    });
    try cases.append(.{
        .input = "())",
        .expected = -1,
    });
    try cases.append(.{
        .input = "))(",
        .expected = -1,
    });
    try cases.append(.{
        .input = ")))",
        .expected = -3,
    });
    try cases.append(.{
        .input = ")())())",
        .expected = -3,
    });

    for (cases.items) |case| {
        try std.testing.expectEqual(case.expected, part1(case.input));
    }
}

test "part2" {
    const Case = struct {
        input: []const u8,
        expected: isize,
    };
    var cases = std.ArrayList(Case).init(std.testing.allocator);
    defer cases.deinit();

    try cases.append(.{
        .input = ")",
        .expected = 1,
    });
    try cases.append(.{
        .input = "()())",
        .expected = 5,
    });
    try cases.append(.{
        .input = "(((((()))))))))))))))))))))))))))))",
        .expected = 13,
    });

    for (cases.items) |case| {
        try std.testing.expectEqual(case.expected, part2(case.input));
    }
}
